import React from 'react';
import { FaTimes, FaArrowUp } from 'react-icons/fa';
import './StatusPanel.css';

const StatusPanel = ({ downloads, cancelDownload, togglePanel, isPanelVisible, hideCompleted }) => {
  // Сортируем объекты: в процессе скачивания отображаются выше завершенных
  const sortedDownloads = downloads.sort((a, b) => {
    if (a.status === 'completed' && b.status !== 'completed') {
      return 1;
    }
    if (a.status !== 'completed' && b.status === 'completed') {
      return -1;
    }
    return 0;
  });

  // Проверяем, есть ли завершенные загрузки
  const hasCompletedDownloads = downloads.some(download => download.status === 'completed');

  return (
    <div className={`status-panel ${isPanelVisible ? 'visible' : 'hidden'}`}>
      <div className="panel-header">
        <span className="panel-title">{isPanelVisible ? "Очередь загрузки" : "Статус панель"}</span>
        <button className="close-button" onClick={togglePanel}>
          {isPanelVisible ? <FaTimes /> : <FaArrowUp />} {/* Замена иконки */}
        </button>
      </div>
      <div className="panel-content">
        {sortedDownloads.length > 0 ? (
          sortedDownloads.map((download, index) => (
            <div
              key={index}
              className={`download-item ${download.status === 'completed' ? 'completed' : ''} ${download.status === 'error' ? 'error' : ''} ${download.status === 'archiving' ? 'archiving' : ''}`}
            >
              <div className="download-info">
                <span>{download.title} {download.size && download.status === 'downloading' ? `(${download.size} MB)` : ''}</span>
                {download.status === 'downloading' && (
                  <button className="cancel-button" onClick={() => cancelDownload(download.id)}><FaTimes /></button>
                )}
              </div>
              {download.status === 'archiving' && <span className="status">идет создание архива...</span>}
              {download.status === 'downloading' && (
                <div className="progress-bar">
                  <div
                    className="progress"
                    style={{ width: `${download.progress}%` }}
                  ></div>
                </div>
              )}
              {download.status === 'completed' && <span className="completed-status">Готово</span>}
              {download.status === 'error' && <span className="error-status">Ошибка</span>}
            </div>
          ))
        ) : (
          <div className="no-downloads">
            <p>Пока тут ничего нет.</p>
            <p>Здесь будут отображаться страницы, которые загружаются.</p>
          </div>
        )}
      </div>
      {hasCompletedDownloads && (
        <button className="hide-completed-button" onClick={hideCompleted}>
          Скрыть загруженное
        </button>
      )}
    </div>
  );
};

export default StatusPanel;
