import React from 'react';
import { FaTimes, FaClock } from 'react-icons/fa';
import './StatusPanel.css';

const StatusPanel = ({ downloads, cancelDownload, togglePanel, isPanelVisible, hideCompleted }) => {
  return (
    <div className={`status-panel ${isPanelVisible ? 'visible' : 'hidden'}`}>
      <div className="panel-header">
        <h3>Очередь загрузки</h3>
        <button className="close-button" onClick={togglePanel}><FaTimes /></button>
      </div>
      <div className="panel-content">
        {downloads.map((download, index) => (
          <div
            key={index}
            className={`download-item ${download.status === 'completed' ? 'completed' : ''} ${download.status === 'error' ? 'error' : ''}`}
          >
            <div className="download-info">
              <span>{download.title} ({download.size ? `${download.size} MB` : '...'})</span>
              {download.status === 'downloading' && (
                <button className="cancel-button" onClick={() => cancelDownload(download.id)}><FaTimes /></button>
              )}
            </div>
            {download.status === 'downloading' && (
              <div className="progress-bar">
                <div
                  className="progress"
                  style={{ width: `${download.progress}%` }}
                ></div>
              </div>
            )}
            {download.status === 'completed' && <span className="completed-status">Готово</span>}
            {download.status === 'error' && <span className="error-status">Ошибка</span>}
          </div>
        ))}
      </div>
      <button className="toggle-button" onClick={togglePanel}>
        <FaClock />
      </button>
      <button className="hide-completed-button" onClick={hideCompleted}>
        Скрыть загруженное
      </button>
    </div>
  );
};

export default StatusPanel;
