import React from 'react';
import { FaTimes, FaClock } from 'react-icons/fa';
import './StatusPanel.css';

const StatusPanel = ({ downloads, cancelDownload, togglePanel, isPanelVisible }) => {
  return (
    <div className={`status-panel ${isPanelVisible ? 'visible' : 'hidden'}`}>
      <div className="panel-header">
        <h3>Очередь загрузки</h3>
        <button className="close-button" onClick={togglePanel}><FaTimes /></button>
      </div>
      <div className="panel-content">
        {downloads.map((download, index) => (
          <div key={index} className="download-item">
            <div className="download-info">
              <span>{download.title}</span>
              <button className="cancel-button" onClick={() => cancelDownload(download.id)}><FaTimes /></button>
            </div>
            <div className="progress-bar">
              <div
                className="progress"
                style={{ width: `${download.progress}%` }}
              ></div>
            </div>
          </div>
        ))}
      </div>
      <button className="toggle-button" onClick={togglePanel}>
        <FaClock />
      </button>
    </div>
  );
};

export default StatusPanel;
