import React from 'react';
import './PageTable.css';

const PageTable = ({ pages, handleDownload, selectedPageIds, onSelectionChange }) => {
  const handleCheckboxChange = (event, pageId) => {
    onSelectionChange(pageId, event.target.checked);
  };

  return (
    <table className="page-table">
      <thead>
        <tr>
          <th>Бейдж</th>
          <th>Заголовок</th>
          <th>Ссылка</th>
          <th>Дата изменения</th>
          <th>Действия</th>
        </tr>
      </thead>
      <tbody>
        {pages.map(page => (
          <tr key={page.id}>
            <td>
              <input
                type="checkbox"
                checked={selectedPageIds.includes(page.id)}
                onChange={(event) => handleCheckboxChange(event, page.id)}
              />
            </td>
            <td>
              {page.title}
              {page.descr && (
                <div style={{ fontSize: '12px', color: 'lightgray' }}>{page.descr}</div>
              )}
            </td>
            <td>
              <a href={`/${page.alias}`} target="_blank" rel="noopener noreferrer">/{page.alias}</a>
            </td>
            <td>{page.date}</td>
            <td>
              <button onClick={() => handleDownload(page.id, page.title)}>Скачать</button>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default PageTable;
