import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [sites, setSites] = useState([]);
  const [pages, setPages] = useState([]);
  const [selectedSite, setSelectedSite] = useState(null);

  useEffect(() => {
    // Получаем список сайтов при загрузке компонента
    axios.get('http://localhost:5000/api/sites')
      .then(response => {
        setSites(response.data.result);
      })
      .catch(error => {
        console.error('Ошибка при загрузке сайтов:', error);
      });
  }, []);

  const fetchPages = (projectId) => {
    // Получаем список страниц для выбранного сайта
    axios.get(`http://localhost:5000/api/sites/${projectId}/pages`)
      .then(response => {
        setPages(response.data.result);
        setSelectedSite(projectId);
      })
      .catch(error => {
        console.error('Ошибка при загрузке страниц:', error);
      });
  };

  return (
    <div className="App">
      <h1>Сайты Tilda</h1>
      <ul>
        {sites.map(site => (
          <li key={site.id}>
            {site.title}
            <button onClick={() => fetchPages(site.id)}>Показать страницы</button>
          </li>
        ))}
      </ul>

      {selectedSite && (
        <div>
          <h2>Страницы сайта</h2>
          <ul>
            {pages.map(page => (
              <li key={page.id}>{page.title}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default App;
